    apiKey: "AIzaSyBjUFfkKtHk1PqxWB3XZOYRU1kIgPlkxe4",
    authDomain: "gadgetco-7a9ce.firebaseapp.com",
    projectId: "gadgetco-7a9ce",
    storageBucket: "gadgetco-7a9ce.appspot.com",
    messagingSenderId: "547846736043",
    appId: "1:547846736043:web:7e3bd7083bbaa1a4ab0fee"
  };


// Initialize Firebase
