    apiKey: "AIzaSyD4NtBcmm-ADFm3WLcXl9o49_lyEC8xvDY",
    authDomain: "gadgetco-b78d5.firebaseapp.com",
    projectId: "gadgetco-b78d5",
    storageBucket: "gadgetco-b78d5.appspot.com",
    messagingSenderId: "537220657013",
    appId: "1:537220657013:web:4d0b4efb719bd1097f2d3c"
  };


// Initialize Firebase
