  authDomain: "gadgetco-51fd3.firebaseapp.com",
  projectId: "gadgetco-51fd3",
  storageBucket: "gadgetco-51fd3.appspot.com",
  messagingSenderId: "541213798762",
  appId: "1:541213798762:web:d637c2d1e37fdef395b513"
